targets.expr:
(
  sum by (instance) (
    jvm_memory_used_bytes{application="$application", instance="$instance", area="heap"}
  )
/
  sum by (instance) (
    jvm_memory_max_bytes{application="$application", instance="$instance", area="heap"}
  )
) * 100

(
  hikaricp_connections_active{
    application="$application",
    instance="$instance"
  }
/
  hikaricp_connections_max{
    application="$application",
    instance="$instance"
  }
) * 100
